$(function () {
  var e = document.getElementById("bar-chart").getContext("2d"),
    o = e.createLinearGradient(0, 0, 0, 150);
  o.addColorStop(0.5, "#3f50f6"), o.addColorStop(1, "#3f50f6");
  var t = e.createLinearGradient(0, 0, 0, 150);
  t.addColorStop(0.5, "#e5e9f2"), t.addColorStop(1, "#e5e9f2");
  new Chart(e, {
    type: "bar",
    data: {
      labels: ["Jun1", "Jun2", "Jun3", "Jun4", "Jun5", "Jun6", "Jun7"],
      datasets: [
        {
          label: "Week 1",
          data: [1.1, 1.5, 1.3, 2.2, 1.2, 0.6, 1.8],
          backgroundColor: o,
          hoverBackgroundColor: o,
          hoverBorderWidth: 1,
          hoverBorderColor: "#3f50f6",
          borderDash: [10, 5],
        },
        {
          label: "Week 2",
          data: [1.9, 1, 0.7, 1.8, 1.8, 1.6, 1.2],
          backgroundColor: t,
          hoverBackgroundColor: t,
          hoverBorderWidth: 1,
          hoverBorderColor: "#e5e9f2",
        },
      ],
    },
    options: {
      maintainAspectRatio: !1,
      legend: { display: !1 },
      scales: {
        xAxes: [
          {
            gridLines: { display: !1 },
            barPercentage: 0.7,
            categoryPercentage: 0.6,
            ticks: {
              fontFamily: "'Rubik', sans-serif",
              fontSize: 13,
              fontColor: "#8392a5",
            },
          },
        ],
        yAxes: [
          {
            gridLines: { drawBorder: !1, zeroLineColor: "#f2f3f2" },
            ticks: {
              fontFamily: "'Rubik', sans-serif",
              fontSize: 13,
              fontColor: "#8392a5",
              min: 0,
              max: 2,
              stepSize: 1,
              suggestedMax: 2.5,
              callback: function (e, o, t) {
                return e + "k";
              },
              beginAtZero: !0,
            },
          },
        ],
      },
    },
  }),
    c3.generate({
      bindto: "#campaign",
      data: {
        columns: [
          ["Direct Sales", 25],
          ["Referral Sales", 15],
          ["Afilliate Sales", 10],
          ["Indirect Sales", 15],
        ],
        type: "donut",
        tooltip: { show: !0 },
      },
      donut: { label: { show: !1 }, title: "Sales", width: 40 },
      legend: { hide: !0 },
      color: { pattern: ["#3f50f6", "#02cccd", "#ff3ca6", "#f2f6fb"] },
    });
  d3.select("#campaign .c3-chart-arcs-title").style("font-family", "Rubik");
  var a = new Chartist.Line(
    ".sales",
    {
      labels: ["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"],
      series: [[11, 10, 15, 21, 14, 23, 12]],
    },
    {
      low: 0,
      high: 28,
      showArea: !0,
      fullWidth: !0,
      plugins: [Chartist.plugins.tooltip()],
      axisY: {
        onlyInteger: !0,
        scaleMinSpace: 40,
        offset: 20,
        labelInterpolationFnc: function (e) {
          return e / 1 + "k";
        },
      },
    }
  );
  a.on("draw", function (e) {
    "area" === e.type && e.element.attr({ x1: e.x1 + 0.001 });
  }),
    a.on("created", function (e) {
      e.svg
        .elem("defs")
        .elem("linearGradient", { id: "gradient", x1: 0, y1: 1, x2: 0, y2: 0 })
        .elem("stop", { offset: 0, "stop-color": "rgba(255, 255, 255, 1)" })
        .parent()
        .elem("stop", { offset: 1, "stop-color": "rgba(80, 153, 255, 1)" });
    }),
    $(window).on("resize", function () {
      a.update();
    });
});
